class Solution(object):
    def lengthOfLIS(self, nums):
        d = run(nums)
        return d

#too long time to computer large array of 2500 elements
def run(a):
    print(len(a))
    max_len=0
    for i in range(0,len(a)):
        iter_len=0
        reset=0
        last_max_gap=0
        for j in range(i+1,len(a)):
            print(i,j)
            if a[j]-a[i]>=0:
                if reset == 1:
                    iter_len = 0
                    iter_len+=1
                    reset =0
                    print('reconf')
                else:
                    if(a[j]-a[i] >last_max_gap):
                        #iter_len = 0
                        iter_len+=1
                        print('fnd')
                    else:
                        print('fnd but keep lkng')
                        continue
                if(a[j]-a[i] >last_max_gap):
                    last_max_gap =a[j]-a[i]
            elif iter_len>0:
                reset = 1
                print('reset')
            else:
                print('look')
                continue
        print(str(iter_len)+":"+str(i))
        if iter_len>max_len:
            max_len = iter_len
    return max_len+1#include the elem as well

if __name__=="__main__":
    d = run(a)
    print(d)
